'use strict'

var modbus = require('../../')
var client = modbus.client.tcp.complete({
  'host': "192.168.3.100",// process.argv[2],
  'port': 502,//process.argv[3],
  'logEnabled': true,
  'logLevel': 'debug',
  'logTimestamp': true
})

client.on('connect', function () {
  client.readCoils(/*process.argv[4]*/1,256 /*process.argv[5]*/).then(function (resp) {
    console.log(resp)
  }, console.error)
    .finally(function () {
      client.close()
    })
})

client.on('error', console.error)
client.connect()
