'use strict'

var modbus = require('../..')
var client = modbus.client.tcp.complete({
    'host': "192.168.3.100",// process.argv[2],
    'port': 502,//process.argv[3],
    'unitId': 1,
    'logEnabled': true,
    'logLevel': 'debug'
})

client.on('connect', function () {

  client.readHoldingRegisters(/*process.argv[4]*/13312, 1/*process.argv[5]*/).then(function (resp) {
    console.log(resp)
      console.log(resp.register)
  }, console.error)
    .finally(function () {
      client.close()
    })
})

client.on('error', console.error)
client.connect()
